mapper.select:
select * from wx_user_score
where 1 = 1

	and oId = #{oId}


	and userId = #{userId}


	and score = #{score}


	and eventKey=#{eventKey}

order by addTime desc

	limit #{startRow},#{limitSize}

select ifnull(sum(score),0) from wx_user_score where userId = #{userId}

	and eventKey=#{eventKey}

and addTime between #{startTime} and #{endTime}
